import datetime
import RPi.GPIO as GPIO
import time


class HCSR04():
    """ Sensor class """

    def __init__(self, pin_trigger, pin_echo):
        """ Constructor """
        self._pin_trigger = pin_trigger
        self._pin_echo = pin_echo

        GPIO.setmode(GPIO.BCM)
        GPIO.setup(self._pin_trigger, GPIO.OUT)
        GPIO.setup(self._pin_echo, GPIO.IN)

        GPIO.output(self._pin_trigger, GPIO.LOW)

        self.calibrate()

    def get_value(self):
        GPIO.output(self._pin_trigger, GPIO.HIGH)
        time.sleep(0.00001)
        GPIO.output(self._pin_trigger, GPIO.LOW)
        pulse_start_time = 0

        start_loop = time.time()
        while time.time() - start_loop <= 1 and GPIO.input(self._pin_echo) == 0:
            pulse_start_time = time.time()

        start_loop = time.time()
        while time.time() - start_loop <= 1 and GPIO.input(self._pin_echo) == 1:
            pulse_end_time = time.time()

        pulse_duration = pulse_end_time - pulse_start_time
        return round(pulse_duration * 17150, 2)

    def _calibrate(self):
        time.sleep(2)
