import RPi.GPIO as GPIO


class Led():
    """ Led class """

    def __init__(self, pin):
        """ Constructor """
        self._pin = pin
        self._on = False
        GPIO.setmode(GPIO.BCM)
        GPIO.setup(self._pin, GPIO.OUT)

    def on(self):
        """ turn led on """
        GPIO.output(self._pin, GPIO.HIGH)
        self._on = True

    def off(self):
        """ turn led on """
        GPIO.output(self._pin, GPIO.LOW)
        self._on = False

    def toggle(self):
        """ toggle led """
        if self._on:
            self.off()
        else:
            self.on()
