import RPi.GPIO as GPIO
import time
from threading import Thread

class Button(Thread):
    """ Button class """

    def __init__(self, pin):
        """ Constructor """
        super(Button, self).__init__(name="Button")

        self._pin = pin
        self._listeners = []
        self._state = "off"
        self._start_time = None
        GPIO.setmode(GPIO.BCM)
        GPIO.setup(self._pin, GPIO.IN, pull_up_down=GPIO.PUD_UP)
        self._run = True

    def add_listener(self, function):
        self._listeners.append(function)

    def run(self):
        """ set status of button """
        while self._run:
            if self._state == "off" and not(bool(GPIO.input(self._pin))):
                self._state = "on"
                self._start_time = time.time()
                for listener in self._listeners:
                    listener(self._state)
            elif self._state != "off" and bool(GPIO.input(self._pin)):
                self._state = "off"
                for listener in self._listeners:
                    listener(self._state)

            self._check_long_press()
            time.sleep(0.1)

    def _check_long_press(self):
        if self._state == "on" and time.time() - self._start_time > 10:
            self._state = "long"
            for listener in self._listeners:
                listener(self._state)

    def stop(self):
        self._run = False
